import Layout from "../layouts/Layout";
// import React from "react";
import React, { useState, useEffect } from "react";
// import { useLocation } from "react-router-dom";
// import smoothscroll from "smoothscroll-polyfill";
// import "../styles/Home.scss";
import texts from "../content/texts";
import Button from "../components/Button";
// import portrait from "../public/images/portrait.png";
// import services1 from "../public/images/services1.png";
// import services2 from "../public/images/services2.png";
// import services3 from "../public/images/services3.png";
// import room from "../public/images/gabinet.png";

// smoothscroll.polyfill();

const Index = () => {
  // const [width, setWidth] = useState(window.innerWidth);
  const [width, setWidth] = useState();

  useEffect(() => {
    const handleResize = () => {
      setWidth(window.innerWidth);
    };
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const contactRef = React.createRef();
  const homeRef = React.createRef();

  // const { pathname } = useLocation();

  // useEffect(() => {
  //   let timer = null;
  //   if (window.location.href.includes("#contact")) {
  //     timer = setTimeout(
  //       () => contactRef.current.scrollIntoView({ behavior: "smooth" }),
  //       250
  //     );
  //   }

  //   window.location.href.includes("#home") &&
  //     homeRef.current.scrollIntoView({ behavior: "smooth" });

  //   return () => {
  //     clearTimeout(timer);
  //   };
  // }, [pathname, contactRef, homeRef]);

  return (
    <Layout>
      <main>
        <div className="first-view-wrap">
          <section className="home" ref={homeRef}>
            <div className="portrait-container">
              <img src="/images/portrait.png" alt="" />
            </div>
            <div className="text-container">
              {texts.home.home}
              {width < 533 ? (
                <Button to="/scheduler">
                  <i className="far fa-calendar-check"></i>
                  <span>Umów spotkanie</span>
                </Button>
              ) : null}
            </div>
          </section>
          <section className="about">
            {texts.home.about}
            <Button to="/about">
              <span>Więcej o mnie</span>
            </Button>
          </section>
        </div>
        <section className="services">
          <h1>Pracuję w trzech obszarach:</h1>
          <div className="service">
            <div className="img-container">
              <img src="/images/services1.png" alt="" />
            </div>
            <div className="text-container">
              {texts.home.services.one}
              {width < 1024 ? null : texts.services.descriptions.one.intro}
            </div>
            <Button to="/services1">
              <span>Więcej informacji</span>
            </Button>
          </div>
          <hr />
          <div className="service">
            <div className="img-container">
              <img src="/images/services2.png" alt="" />
            </div>
            <div className="text-container">
              {texts.home.services.two}
              {width < 1024 ? null : texts.services.descriptions.two.intro}
            </div>
            <Button to="/services2">
              <span>Więcej informacji</span>
            </Button>
          </div>
          <hr />
          <div className="service">
            <div className="img-container">
              <img src="/images/services3.png" alt="" />
            </div>
            <div className="text-container">
              {texts.home.services.three}
              {width < 1024 ? null : texts.services.descriptions.three.intro}
            </div>
            <Button to="/services3">
              <span>Więcej informacji</span>
            </Button>
          </div>
        </section>
        <section className="contact" ref={contactRef}>
          <h1>Kontakt</h1>
          <div className="contact-wrap">
            {/* <div className="img-wrap"> */}
            <div className="img-container">
              <img src="/images/gabinet.png" alt="" />
            </div>
            {/* </div> */}
            <div className="details-wrap">
              <div className="text-container">
                <table>
                  <tbody>
                    <tr>
                      <td>
                        <i className="fas fa-id-card"></i>
                      </td>
                      <td>
                        <p>{texts.home.contact.companyLine1}</p>
                        <p>{texts.home.contact.companyLine2}</p>
                        <p>{texts.home.contact.companyLine3}</p>
                      </td>
                    </tr>
                    <tr>
                      <td>
                        <i className="fas fa-home"></i>
                      </td>
                      <td>
                        <p>{texts.home.contact.addressLine1}</p>
                        <p>{texts.home.contact.addressLine2}</p>
                      </td>
                    </tr>
                    <tr>
                      <td>
                        <i className="fas fa-envelope"></i>
                      </td>
                      <td>
                        <a
                          className="row"
                          rel="noopener noreferrer"
                          href={`mailto:${texts.home.contact.email}`}
                        >
                          <p>{texts.home.contact.email}</p>
                        </a>
                      </td>
                    </tr>
                    <tr>
                      <td>
                        <i className="fas fa-phone-alt"></i>
                      </td>
                      <td>
                        <a
                          className="row"
                          rel="noopener noreferrer"
                          href={`tel:${texts.home.contact.phone}`}
                        >
                          <p>{texts.home.contact.phone}</p>
                        </a>
                      </td>
                    </tr>
                    <tr>
                      <td>
                        <i className="fas fa-money-bill-wave"></i>
                      </td>
                      <td>
                        <p>{texts.home.contact.accountNumberLine1}</p>
                        <p>{texts.home.contact.accountNumberLine2}</p>
                      </td>
                    </tr>
                    <tr>
                      <td>
                        <i className="fab fa-facebook-square"></i>
                      </td>
                      <td>
                        <p>Zapraszam także do odwiedzenia</p>
                        <p>
                          <a
                            className="row"
                            target="_blank"
                            rel="noopener noreferrer"
                            href="https://www.facebook.com"
                          >
                            mojego profilu na Facebooku
                          </a>
                        </p>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
